  {gallery_name: "elee.mosynary Gallery", address: "645 W. 18th St.", latitude: 41.8574033, longitude: -87.6436419, phone: nil, website: "www.eleegallery.com", email: nil, owner_operator: nil, medium: nil, description: "We are a soulful community of entrepreneurial creatives.<br><br>\n\nThe gallery houses several projects, all of which share the mission of promoting lesser-known artists and brands, as well as positive art forms and lifestyles.<br><br> \n\nThis gallery pays homage to the culture of Hip-Hop; prizing the authentic, the crafty, the original and most important, community.<br><br>\n\nWe honor the DJ, the MC, the dancer, street artists, street language, street knowledge and street entrepreneurialism.\n", thumbnail_file_name: "elee-mosynary-signage.jpg", thumbnail_content_type: "image/jpeg", thumbnail_file_size: 22671, thumbnail_updated_at: "2017-09-18 21:39:17", city: "Chicago", state: "IL", zip: "60616", hours: "By appointment only.", current: false},
